function n(){const e=new IntersectionObserver(t=>{t.forEach(s=>{s.isIntersecting&&(s.target.classList.add("visible"),e.unobserve(s.target))})},{threshold:.2});document.querySelectorAll(".animate-on-scroll").forEach(t=>{e.observe(t)})}document.addEventListener("DOMContentLoaded",n);document.addEventListener("DOMContentLoaded",()=>{const e=document.querySelector(".header"),t=50;e&&(window.scrollY>t&&e.classList.add("transparent"),window.addEventListener("scroll",()=>{window.scrollY>t?e.classList.add("transparent"):e.classList.remove("transparent")}))});
